select ord_no as orderNo, user_custom_order_no as userCustomOrderNo, status, create_by as creator, create_on as createOn, carrier_no as carrierNo, track_no as trackNo, category, channel, from_address_line1 as fromAddressLine1, from_address_line2 as fromAddressLine2, from_address_line3 as fromAddressLine3, from_zipcode as fromZipCode, from_whom as fromName, from_contact as fromContact, to_address_line1 as toAddressLine1, to_address_line2 as toAddressLine2, to_address_line3 as toAddressLine3, to_zipcode as toZipCode, to_whom as toName, to_contact as toContact, on_behalf as onBehalf, collect_num as collectNum from l_manual_order ord
and ord.category=#{type}
and ord.status=#{status}
and ord.ord_no=#{ordno}
and ord.create_by=#{creator}
and ord.channel=#{channelCode}
and ord.track_no=#{trackNo}
and ord.user_custom_order_no=#{userCustomOrderNo}
and ord.pickup=#{pickup}
and ord.create_on >= #{from}
and ord.create_on <= #{to}
order by ord.update_on desc

