mapper.select:
select sku, img1, img2, img3
from l_productions
where sku = #{sku}

select sku, img1, img2, img3
from l_productions
where sku = #{sku}

select product.*,content.sku as subSku,content.dySku as subDySku,content.name as subName,content.location as subLocation,content.num as subNum from l_productions product left join l_product_shelf_layout content on(product.sku=content.sku and product.dy_sku=content.dySku) where status = #{status}
and createdBy = #{username}
and product.sku like CONCAT('%', #{sku}, '%')
and product.dy_sku like CONCAT('%', #{dySku}, '%')
and product.product_name like CONCAT('%', #{name}, '%')

select product.*, content.sku as subSku, content.dySku as subDySku, content.name as subName, content.location as subLocation, content.num as subNum from l_productions product left join l_product_shelf_layout content on (product.sku = content.sku and product.dy_sku = content.dySku) where status = #{status}
and createdBy = #{creator}
and product.sku like CONCAT('%', #{sku}, '%')
and product.dy_sku like CONCAT('%', #{dySku}, '%')
and product.product_name like CONCAT('%', #{name}, '%')

mapper.update:
update l_productions
set status=#{status}
where sku = #{sku}
